import React, { Component } from 'react';
import Login from './Login';
import SignUp from './SignUp'


class SignUpLogin extends Component {

    constructor(props) {
        super(props);
        this.state = {
            login: "",
            signup: ""

        }
        this.getInitialState = this.getInitialState.bind(this);
        this.switch = this.switch.bind(this);
    }
    getInitialState() {
        return { signup: false, login: true }
    }

    switch(word) {
        var signup, login;
        if (word == "signup") { signup = true; login = false; }
        else { login = true; signup = false; }
        return this.setState({ login: Login, signup: SignUp })
    }
    render() {
        var self = this;
        return (
            <div>
                <div id="buttons">
                    <p id="signupButton" onClick={self.switch.bind(null, "signup")} className={self.state.signup ? '#6cc091' : "green"}>Sign In</p>
                    <p id="loginButton" onClick={self.switch.bind(null, "login")} className={self.state.login ? "#6cc091" : "green"}> Login</p>
                </div>
                {self.state.signup ? <SignUp /> : null}
                {self.state.login ? <Login /> : null}

            </div>

        )


    }
}


export default SignUpLogin;
