import React, { Component } from 'react';
import Login from './Login';
import SignUp from './SignUp'


class SignUpLogin extends Component {

    constructor(props) {
        super(props);
        // this.getInitialState = this.getInitialState.bind(this);
        this.state = {
            login :"",
            signup :""
        }
        this.switch = this.switch.bind(this);
    }
    componentDidMount() {
        return { signup: false, login: true }
    }

    switch(word) {
        var login ,signup
        if (word === "signup") { signup = true; login = false; }
        else { login = true; signup = false; }
        return this.setState({ login: login, signup: signup })
    }
    render() {
           return (
            <div>
                <div id="buttons">
                    <p id="signupButton" onClick={this.switch.bind(null, "signup")} className={this.state.signup ? '#6cc091' : "green"}>Sign In</p>
                    <p id="loginButton" onClick={this.switch.bind(null, "login")} className={this.state.login ? "#6cc091" : "green"}> Login</p>
                </div>
                {this.state.signup ? <SignUp /> : null}
                {this.state.login ? <Login /> : null}

            </div>

        )


    }
}


export default SignUpLogin;
